print(shufle(str1: SimpleXoR.decrypt(result[0]), str2: SimpleXoR.decrypt(result[1]), str3: SimpleXoR.decrypt(result[2])))



func shufle(str1: String, str2: String, str3: String) -> String {
    let arr1 = Array(str1)
    let arr2 = Array(str2)
    let arr3 = Array(str3)
    var result = ""
    for i in 0..<str3.count {
        result += String(arr1[i])
        result += String(arr2[i])
        result += String(arr3[i])
    }
    return result
}
